import React from 'react';
import { Card, CardContent } from './ui/card';
import { FileText, GraduationCap, ScrollText, User ,X} from 'lucide-react';
import AcademicDocuments from './documents/AcademicDocuments'

const Applications = ({onClose}) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const [activeItem, setActiveItem] = useState('home');
  const [currentContent, setCurrentContent] = useState(null);
  const [showDocuments, setShowDocuments] = useState(false);
  const categories = [
    {
      title: 'Financial Documents',
      icon: FileText,
      description: 'Bank statements, tax returns, and financial records'
    },
    {
      title: 'Academic Documents',
      icon: GraduationCap,
      description: 'Transcripts, degrees, and certificates'
    },
    {
      title: 'Test Scores',
      icon: ScrollText,
      description: 'Standardized test results and assessments'
    },
    {
      title: 'Personal Documents',
      icon: User,
      description: 'ID cards, passports, and personal information'
    }
  ];

  const handleCatClick = (item) => {
    setActiveItem(item.id);
    if (item.title === 'Academic Documents') {
      setShowDocuments(true);
    }
  };

   return (
    <div className="fixed inset-0 bg-white z-50">
      <div className="p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Applications</h1>
          <button 
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
          >
            <X size={24} />
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {categories.map((category, index) => (
            <Card key={index} className="p-4 hover:shadow-lg transition-shadow cursor-pointer" onClick={(e) => {
                  e.preventDefault();
                  handleCatClick(item);
                }}>
              <CardContent className="flex flex-col items-center space-y-4 pt-6">
                <category.icon className="w-12 h-12 text-blue-600" />
                <div className="text-center">
                  <h2 className="text-xl font-semibold mb-2">{category.title}</h2>
                  <p className="text-sm text-gray-600">{category.description}</p>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Applications;
