import React from 'react';
import { User, LogOut, Bell, Search } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const Navbar = ({ isLoggedIn, user, onLogout }) => {
  const navigate = useNavigate();

  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          {/* Left Section: Logo and Navigation */}
          <div className="flex items-center">
            <span className="text-xl font-bold">Social Awareness</span>
            <div className="ml-10 flex space-x-4">
              <a href="/" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Home
              </a>
              <a href="/campaigns" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Campaigns
              </a>
              <a href="/businesses" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Businesses
              </a>
            </div>
          </div>

          {/* Right Section: Search, Notifications, User Actions */}
          <div className="flex items-center space-x-4">
            <button className="p-2 hover:bg-gray-100 rounded-full">
              <Search size={20} />
            </button>

            {isLoggedIn && (
              <button className="p-2 hover:bg-gray-100 rounded-full">
                <Bell size={20} />
              </button>
            )}

            {isLoggedIn ? (
              <div className="flex items-center space-x-4">
                <button className="p-2 hover:bg-gray-100 rounded-full">
                  <User size={20} />
                </button>
                <button
                  onClick={onLogout}
                  className="flex items-center space-x-2 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
                >
                  <LogOut size={20} />
                  <span>Logout</span>
                </button>
              </div>
            ) : (
              <div className="flex space-x-4">
                <button
                  onClick={() => navigate('/login')}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  Login
                </button>
                <button
                  onClick={() => navigate('/register')}
                  className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                >
                  Register
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
