import React, { useState } from 'react';
import { User, LogOut, Bell, Search, PlusCircle } from 'lucide-react';
import {
  Card,
  CardHeader,
  CardTitle,
  CardContent,
  CardFooter,
} from './ui/card';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from './ui/dialog';

import { ThumbsUp, MessageCircle, Share2 } from 'lucide-react';


// Navbar Component
const Navbar = ({ isLoggedIn, onLogout }) => {
  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <span className="text-xl font-bold">Social Awareness</span>
            <div className="ml-10 flex space-x-4">
              <a href="/" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Home
              </a>
              <a href="/campaigns" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Campaigns
              </a>
              <a href="/businesses" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Businesses
              </a>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            <button className="p-2 hover:bg-gray-100 rounded-full">
              <Search size={20} />
            </button>
            <button className="p-2 hover:bg-gray-100 rounded-full">
              <Bell size={20} />
            </button>

            {isLoggedIn ? (
              <div className="flex items-center space-x-4">
                <button className="p-2 hover:bg-gray-100 rounded-full">
                  <User size={20} />
                </button>
                <button
                  onClick={onLogout}
                  className="flex items-center space-x-2 p-2 hover:bg-gray-100 rounded-full"
                >
                  <LogOut size={20} />
                </button>
              </div>
            ) : (
              <div className="flex space-x-4">
                {/* Remove the login and register dialogs and buttons directly */}
                <button className="px-4 py-2 bg-blue-600 text-white rounded">
                  Login
                </button>
                <button className="px-4 py-2 bg-green-600 text-white rounded">
                  Register
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

// Campaign Card Component
const CampaignCard = ({ campaign }) => {
  return (
    <Card className="mb-4">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span>{campaign.title}</span>
          <span className="text-sm text-gray-500">Category: {campaign.category}</span>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <p className="text-gray-700">{campaign.description}</p>
        {campaign.image_url && (
          <img src="/api/placeholder/400/300" alt="Campaign" className="w-full rounded-lg mt-4" />
        )}
      </CardContent>
      <CardFooter className="flex justify-between">
        <button className="text-gray-600 hover:text-blue-600"><ThumbsUp size={20} /></button>
        <button className="text-gray-600 hover:text-blue-600">Comment</button>
        <button className="text-gray-600 hover:text-blue-600">Share</button>
      </CardFooter>
    </Card>
  );
};

// Advertisement Card Component
const AdvertisementCard = ({ ad }) => {
  return (
    <Card className="mb-4 bg-gray-50">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span>{ad.title}</span>
          <span className="text-sm text-blue-600">Sponsored</span>
        </CardTitle>
      </CardHeader>
      <CardContent>
        <p className="text-gray-700">{ad.content}</p>
      </CardContent>
      <CardFooter>
        <a href={ad.website} className="text-blue-600 hover:text-blue-700">
          Learn More
        </a>
      </CardFooter>
    </Card>
  );
};

// Main HomePage Component
const HomePage = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [advertisements, setAdvertisements] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar isLoggedIn={isLoggedIn} onLogout={() => setIsLoggedIn(false)} />

      <main className="max-w-7xl mx-auto py-6 px-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Left Sidebar */}
          <div className="md:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle>Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <button className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded">
                  <PlusCircle size={20} />
                  <span>Create Campaign</span>
                </button>
                <button className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded">
                  <PlusCircle size={20} />
                  <span>Create Advertisement</span>
                </button>
              </CardContent>
            </Card>
          </div>

          {/* Main Content */}
          <div className="md:col-span-2">
            <div className="space-y-6">
              {/* Sample Campaign */}
              <CampaignCard
                campaign={{
                  title: 'Save the Ocean Campaign',
                  category: 'Environment',
                  description:
                    'Join us in our mission to clean up the oceans and protect marine life.',
                  image_url: 'ocean-cleanup.jpg',
                }}
              />

              {/* Sample Advertisement */}
              <AdvertisementCard
                ad={{
                  title: 'Eco-Friendly Products',
                  content:
                    'Discover our range of sustainable products that help reduce plastic waste.',
                  website: 'https://eco-store.com',
                }}
              />
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
