import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './components/Homepage';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Navbar from './components/Navbar';  // Make sure to import Navbar
import AdvertisementForm from './components/AdvertisementForm';  // Import the advertisement form component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check if user is logged in and get user details
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');

    if (token && userData) {
      setIsLoggedIn(true);
      setUser(JSON.parse(userData));
    } else {
      setIsLoggedIn(false);
      setUser(null);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsLoggedIn(false);
    setUser(null);
  };

  return (
    <BrowserRouter>
      <Navbar isLoggedIn={isLoggedIn} user={user} onLogout={handleLogout} />
      <Routes>
        <Route
          path="/"
          element={<HomePage isLoggedIn={isLoggedIn} onLogout={handleLogout} />}
        />
        <Route
          path="/login"
          element={
            !isLoggedIn ? (
              <Login onLoginSuccess={(userData) => {
                setIsLoggedIn(true);
                setUser(userData);
              }} />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        <Route
          path="/register"
          element={
            !isLoggedIn ? (
              <Register onRegisterSuccess={(userData) => {
                setIsLoggedIn(true);
                setUser(userData);
              }} />
            ) : (
              <Navigate to="/" />
            )
          }
        />
        {/* Add the AdvertisementForm route */}
        <Route
          path="/advertisement-form"
          element={
            isLoggedIn ? (
              <AdvertisementForm />  // This will render your advertisement form component
            ) : (
              <Navigate to="/login" />  // Redirect to login if not logged in
            )
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
