import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PlusCircle } from 'lucide-react';
import AdvertisementCard from './AdvertisementCard';
import CampaignCard from './CampaignCard';
import Navbar from './Navbar';
import AdvertisementForm from './pages/AdvertisementForm';
import CampaignForm from './pages/CampaignForm';

const HomePage = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [advertisements, setAdvertisements] = useState([]);
  const [activeForm, setActiveForm] = useState(null); // 'campaign' or 'advertisement'
  const [isLoading, setIsLoading] = useState(false);

  // Fetch campaigns and advertisements
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const [campaignsRes, adsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/campaigns'),
          axios.get('http://localhost:5000/api/advertisements'),
        ]);

        setCampaigns(campaignsRes.data);
        setAdvertisements(adsRes.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCreateCampaign = (data) => {
    setCampaigns([...campaigns, data]);
    setActiveForm(null); // Close the form
  };

  const handleCreateAdvertisement = (data) => {
    setAdvertisements([...advertisements, data]);
    setActiveForm(null); // Close the form
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar isLoggedIn={isLoggedIn} onLogout={() => setIsLoggedIn(false)} />

      <main className="max-w-7xl mx-auto py-6 px-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Left Sidebar */}
          <div className="md:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle>Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <button className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded"  onClick={() => setActiveForm('campaign')}>
                  <PlusCircle size={20} />
                  <span>Create Campaign</span>
                </button>
                <button className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded"  onClick={() => setActiveForm('advertisement')}>
                  <PlusCircle size={20} />
                  <span>Create Advertisement</span>
                </button>
              </CardContent>
            </Card>
          </div>

          {/* Main Content */}
          <div className="md:col-span-2">
            <div className="space-y-6">
              {isLoading ? (
                <div>Loading...</div>
              ) : (
                <>
                  {/* Campaigns */}
                  {campaigns.map(campaign => (
                    <CampaignCard
                      key={campaign._id}
                      campaign={campaign}
                    />
                  ))}

                  {/* Advertisements */}
                  {advertisements.map(ad => (
                    <AdvertisementCard
                      key={ad._id}
                      ad={ad}
                    />
                  ))}
                </>
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
