import React from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { X } from 'lucide-react';

const BusinessForm = ({ onSubmit }) => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const navigate = useNavigate();

  const submitForm = async (data) => {
    try {
      const token = localStorage.getItem('authToken'); // Get the token from localStorage

      const response = await fetch('/api/businesses', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`, // Include the token in the Authorization header
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        throw new Error('Failed to create business');
      }

      const result = await response.json();
      // Optionally handle the result, e.g., navigate to another page or show a success message
      console.log(result);
      navigate('/advertisement-form'); // Redirect to homepage after success
    } catch (error) {
      console.error('Error:', error);
      // Optionally handle the error (e.g., show an error message to the user)
    }
  };

  return (
    <form
      onSubmit={handleSubmit(submitForm)}
      className="w-1/3 mx-auto bg-white shadow-md rounded-lg  p-1 space-y-4 border border-gray-300"
      style={{ minWidth: '500px' }}
    >
      <div className="relative">
        <button
          onClick={(e) => {
            e.preventDefault(); // Prevent form submission
            navigate('/'); // Redirect to homepage
          }}
          className="absolute top-1 right-4 p-2 rounded-full hover:bg-gray-200 transition-colors"
        >
          <X size={24} className="text-gray-600" />
        </button>
      </div>

      <div className="mt-10">
        <label className="block text-sm font-medium text-gray-700">Business Name</label>
        <input
          type="text"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('name', { required: 'Business name is required' })}
        />
        {errors.name && <p className="text-red-500 text-sm">{errors.name.message}</p>}
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Description</label>
        <textarea
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          rows="4"
          {...register('description')}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Contact Info</label>
        <input
          type="text"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('contact_info')}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Website</label>
        <input
          type="url"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('website')}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Category</label>
        <input
          type="text"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('category')}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Location</label>
        <input
          type="text"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('location')}
        />
      </div>

      <div>
        <button
          type="submit"
          className="w-full py-2 px-4 bg-blue-600 text-white font-bold rounded-md hover:bg-blue-700 transition-colors"
        >
          Create Business
        </button>
      </div>
    </form>
  );
};

export default BusinessForm;
