import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { PlusCircle } from 'lucide-react';
import AdvertisementCard from './AdvertisementCard';
import CampaignCard from './CampaignCard';
import AdvertisementForm from './pages/AdvertisementForm';
import CampaignForm from './pages/CampaignForm';

import {
  Card,
  CardHeader,
  CardTitle,
  CardContent,
} from './ui/card';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from './ui/dialog';

const HomePage = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [advertisements, setAdvertisements] = useState([]);
  const [activeForm, setActiveForm] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [businesses, setBusinesses] = useState([]); // Add this if needed for AdvertisementForm

  const navigate = useNavigate();

  // Check if the user is authenticated
  const isAuthenticated = () => {
    const token = localStorage.getItem('token');
    return !!token;
  };

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    }
  }, [navigate]);

  // Fetch campaigns and advertisements
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const token = localStorage.getItem('token');
        const [campaignsRes, adsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/campaigns'),
          axios.get('http://localhost:5000/api/advertisements')
        ]);

        setCampaigns(campaignsRes.data);
        setAdvertisements(adsRes.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCreateCampaign = async (data) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/campaigns', data, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCampaigns([...campaigns, response.data]);
      setActiveForm(null); // Close the dialog
    } catch (error) {
      console.error('Error creating campaign:', error);
      // Handle error (you might want to show an error message to the user)
    }
  };

  const handleCreateAdvertisement = async (data) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/advertisements', data);

      setAdvertisements([...advertisements, response.data]);
      setActiveForm(null); // Close the dialog
    } catch (error) {
      console.error('Error creating advertisement:', error);
      // Handle error
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <main className="max-w-7xl mx-auto py-6 px-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Left Sidebar */}
          <div className="md:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle>Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <button
                  className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded"
                  onClick={() => setActiveForm('campaign')}
                >
                  <PlusCircle size={20} />
                  <span>Create Campaign</span>
                </button>
                <button
                  className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded"
                  onClick={() => setActiveForm('advertisement')}
                >
                  <PlusCircle size={20} />
                  <span>Create Advertisement</span>
                </button>
              </CardContent>
            </Card>

            {/* Advertisement Cards */}
            <div className="mt-4 space-y-4">
              <h2 className="text-xl font-semibold">Advertisements</h2>
              {advertisements.map((ad) => (
                <AdvertisementCard key={ad._id} ad={ad} />
              ))}
            </div>
          </div>

          {/* Main Content */}
          <div className="md:col-span-2">
            <div className="space-y-6">
              {isLoading ? (
                <div>Loading...</div>
              ) : (
                <>
                  <h2 className="text-xl font-semibold">Campaigns</h2>
                  {campaigns.map((campaign) => (
                    <CampaignCard key={campaign._id} campaign={campaign} />
                  ))}
                </>
              )}
            </div>
          </div>
        </div>

        {/* Dialog for forms */}
        <Dialog open={Boolean(activeForm)} onOpenChange={(open) => !open && setActiveForm(null)}>
          <DialogContent className="sm:max-w-[600px]">
            <DialogHeader>
              <DialogTitle>
                {activeForm === 'campaign' ? 'Create Campaign' : 'Create Advertisement'}
              </DialogTitle>
            </DialogHeader>

            {activeForm === 'campaign' && (
              <CampaignForm 
                onCreate={handleCreateCampaign} 
                onClose={() => setActiveForm(null)}
              />
            )}
            {activeForm === 'advertisement' && (
              <AdvertisementForm 
                onSubmit={handleCreateAdvertisement}
                businesses={businesses}
                onClose={() => setActiveForm(null)}
              />
            )}
          </DialogContent>
        </Dialog>
      </main>
    </div>
  );
};

export default HomePage;
