import React, { useState } from 'react';
import { User, LogOut, Bell, Search, PlusCircle } from 'lucide-react';
import {
  Card,
  CardHeader,
  CardTitle,
  CardContent,
  CardFooter,
} from "./ui/card";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "./ui/dialog";

// Navbar Component
const Navbar = ({ isLoggedIn, onLogout }) => {
  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <span className="text-xl font-bold">Social Awareness</span>
            <div className="ml-10 flex space-x-4">
              <a href="/" className="text-gray-700 hover:text-blue-600 px-3 py-2">Home</a>
              <a href="/campaigns" className="text-gray-700 hover:text-blue-600 px-3 py-2">Campaigns</a>
              <a href="/businesses" className="text-gray-700 hover:text-blue-600 px-3 py-2">Businesses</a>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            <button className="p-2 hover:bg-gray-100 rounded-full">
              <Search size={20} />
            </button>
            <button className="p-2 hover:bg-gray-100 rounded-full">
              <Bell size={20} />
            </button>

            {isLoggedIn ? (
              <div className="flex items-center space-x-4">
                <button className="p-2 hover:bg-gray-100 rounded-full">
