import React, { useState } from 'react';
import { User, LogOut, Bell, Search } from 'lucide-react';
import Login from './auth/Login';
import Register from './auth/Register';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';

// Navbar Component
const Navbar = ({ isLoggedIn, onLogout }) => {
  const [activeModal, setActiveModal] = useState(null); // 'login' or 'register'

  const openModal = (modalType) => setActiveModal(modalType);
  const closeModal = () => setActiveModal(null);

  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between h-16">
          {/* Left Section: Logo and Navigation */}
          <div className="flex items-center">
            <span className="text-xl font-bold">Social Awareness</span>
            <div className="ml-10 flex space-x-4">
              <a href="/" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Home
              </a>
              <a href="/campaigns" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Campaigns
              </a>
              <a href="/businesses" className="text-gray-700 hover:text-blue-600 px-3 py-2">
                Businesses
              </a>
            </div>
          </div>

          {/* Right Section: Search, Notifications, User Actions */}
          <div className="flex items-center space-x-4">
            <button className="p-2 hover:bg-gray-100 rounded-full">
              <Search size={20} />
            </button>
            <button className="p-2 hover:bg-gray-100 rounded-full">
              <Bell size={20} />
            </button>

            {isLoggedIn ? (
              <div className="flex items-center space-x-4">
                <button className="p-2 hover:bg-gray-100 rounded-full">
                  <User size={20} />
                </button>
                <button
                  onClick={onLogout}
                  className="flex items-center space-x-2 p-2 hover:bg-gray-100 rounded-full"
                >
                  <LogOut size={20} />
                </button>
              </div>
            ) : (
              <div className="flex space-x-4">
                <button
                  onClick={() => openModal('login')}
                  className="px-4 py-2 bg-blue-600 text-white rounded"
                >
                  Login
                </button>
                <button
                  onClick={() => openModal('register')}
                  className="px-4 py-2 bg-green-600 text-white rounded"
                >
                  Register
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Dialog for Login */}
      {activeModal === 'login' && (
        <Dialog open={true} onOpenChange={closeModal}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Login</DialogTitle>
            </DialogHeader>
            <Login onClose={closeModal} />
          </DialogContent>
        </Dialog>
      )}

      {/* Dialog for Register */}
      {activeModal === 'register' && (
        <Dialog open={true} onOpenChange={closeModal}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Register</DialogTitle>
            </DialogHeader>
            <Register onClose={closeModal} />
          </DialogContent>
        </Dialog>
      )}
    </nav>
  );
};

export default Navbar;
