import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { X } from 'lucide-react';
import BusinessForm from './BusinessForm'; // Import the BusinessForm component

const AdvertisementForm = ({ onSubmit, businesses, onBusinessSelect, onCreateBusiness }) => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState(true);

  // State to handle BusinessForm visibility
  const [showBusinessForm, setShowBusinessForm] = useState(false);

  // State for filtered businesses
  const [filteredBusinesses, setFilteredBusinesses] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem('user_id'); // Retrieve the user ID from local storage
    if (userId && businesses) {
      // Filter businesses based on the user ID
      const userBusinesses = businesses.filter(business => business.user_id === userId);
      setFilteredBusinesses(userBusinesses);
    }
  }, [businesses]);

  const handleBusinessChange = (e) => {
    onBusinessSelect(e.target.value); // Trigger the parent callback on business selection
  };

  const handleAddBusinessClick = () => {
    setShowBusinessForm(true); // Show the BusinessForm when clicked
  };

  const handleCloseForm = () => {
    setIsVisible(false); // Hide the form when close button is clicked
  };

  if (!isVisible) {
    return null; // Do not render the form if it's not visible
  }

  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className="w-1/3 mx-auto bg-white shadow-md rounded-lg p-6 space-y-0 border border-gray-300"
      style={{ minWidth: '600px' }}
    >
      <div className="relative">
        <button
          onClick={(e) => {
            e.preventDefault(); // Prevent form submission
            handleCloseForm(); // Call the handleCloseForm function
          }}
          className="absolute top-0 right-4 p-2 rounded-full hover:bg-gray-200 transition-colors"
        >
          <X size={24} className="text-gray-600" />
        </button>
      </div>

      {/* Show BusinessForm if showBusinessForm is true */}
      {showBusinessForm ? (
        <BusinessForm onClose={() => setShowBusinessForm(false)} />
      ) : (
        <>
          {/* Select Business */}
          <div>
            <label className="block text-sm font-medium text-gray-700">Select Business</label>
            <select
              className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              onChange={handleBusinessChange}
            >
              <option value="">Select a business</option>
              {filteredBusinesses && filteredBusinesses.map((business) => (
                <option key={business._id} value={business._id}>
                  {business.name}
                </option>
              ))}
            </select>
            <p>or </p>
            <button
              type="button"
              onClick={handleAddBusinessClick}
              className="mt-1 py-2 px-4 bg-green-600 text-white font-bold rounded-md hover:bg-green-700 transition-colors"
            >
              Add New Business
            </button>
          </div>
        </>
      )}

      {/* Other fields */}
      <div>
        <label className="block text-sm font-medium text-gray-700">Title</label>
        <input
          type="text"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('title', { required: 'Title is required' })}
        />
        {errors.title && <p className="text-red-500 text-sm">{errors.title.message}</p>}
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Content</label>
        <textarea
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          rows="4"
          {...register('content', { required: 'Content is required' })}
        />
        {errors.content && <p className="text-red-500 text-sm">{errors.content.message}</p>}
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Start Date</label>
        <input
          type="date"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('start_date')}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">End Date</label>
        <input
          type="date"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('end_date')}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Budget</label>
        <input
          type="number"
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('budget', { required: 'Budget is required' })}
        />
        {errors.budget && <p className="text-red-500 text-sm">{errors.budget.message}</p>}
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Status</label>
        <select
          className="mt-1 p-2 w-full border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          {...register('status', { required: 'Status is required' })}
        >
          <option value="pending">Pending</option>
          <option value="active">Active</option>
          <option value="completed">Completed</option>
        </select>
        {errors.status && <p className="text-red-500 text-sm">{errors.status.message}</p>}
      </div>

      <div>
        <button
          type="submit"
          className="w-full py-2 px-4 bg-blue-600 text-white font-bold rounded-md hover:bg-blue-700 transition-colors"
        >
          Create Advertisement
        </button>
      </div>
    </form>
  );
};

export default AdvertisementForm;
