import React, { useState } from 'react';
import { User, LogOut, Bell, Search, PlusCircle } from 'lucide-react';
import {
  Card,
  CardHeader,
  CardTitle,
  CardContent,
  CardFooter,
} from './ui/card';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from './ui/dialog';

import { ThumbsUp, MessageCircle, Share2 } from 'lucide-react';
import AdvertisementCard from './AdvertisementCard';
import CampaignCard from './CampaignCard';
import Navbar from './Navbar';
import AdvertisementForm from './pages/AdvertisementForm';

// Main HomePage Component
const HomePage = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [advertisements, setAdvertisements] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // States for handling form visibility
  const [showCampaignForm, setShowCampaignForm] = useState(false);
  const [showAdvertisementForm, setShowAdvertisementForm] = useState(false);

  const handleCreateCampaign = (data) => {
    // Add logic to handle campaign form submission
    setCampaigns([...campaigns, data]);
    setShowCampaignForm(false); // Close the form after submission
  };

  const handleCreateAdvertisement = (data) => {
    // Add logic to handle advertisement form submission
    setAdvertisements([...advertisements, data]);
    setShowAdvertisementForm(false); // Close the form after submission
  };


  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar isLoggedIn={isLoggedIn} onLogout={() => setIsLoggedIn(false)} />

      <main className="max-w-7xl mx-auto py-6 px-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Left Sidebar */}
          <div className="md:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle>Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-2">
                <button className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded" onClick={() => setShowCampaignForm(true)}>
                  <PlusCircle size={20} />
                  <span>Create Campaign</span>
                </button>
                <button className="flex items-center space-x-2 w-full p-2 hover:bg-gray-100 rounded" onClick={() => setShowAdvertisementForm(true)}>
                  <PlusCircle size={20} />
                  <span>Create Advertisement</span>
                </button>
              </CardContent>
            </Card>
          </div>

          {/* Main Content */}
          <div className="md:col-span-2">
            <div className="space-y-6">
              {/* Sample Campaign */}
              <CampaignCard
                campaign={{
                  title: 'Save the Ocean Campaign',
                  category: 'Environment',
                  description:
                    'Join us in our mission to clean up the oceans and protect marine life.',
                  image_url: 'ocean-cleanup.jpg',
                }}
              />

              {/* Sample Advertisement */}
              <AdvertisementCard
                ad={{
                  title: 'Eco-Friendly Products',
                  content:
                    'Discover our range of sustainable products that help reduce plastic waste.',
                  website: 'https://eco-store.com',
                }}
              />
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default HomePage;
