import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import Button from '../ui/button';
import Input from '../ui/input';
import Label from '../ui/label';
import { Alert, AlertDescription } from '../ui/alert';
import { 
  FileUp, 
  Plus,
  FileText,
  GraduationCap,
  X,
  Upload,
  Check,
  AlertCircle
} from 'lucide-react';

const AcademicDocuments = () => {
  const [documents, setDocuments] = React.useState([]);
  const [uploading, setUploading] = React.useState(false);
  const fileInputRef = React.useRef(null);

  // State to store the selected document type
  const [selectedDocumentType, setSelectedDocumentType] = React.useState('other');

  const documentTypes = [
    { id: 'transcript', label: 'Academic Transcript', icon: GraduationCap },
    { id: 'degree', label: 'Degree Certificate', icon: FileText },
    { id: 'language', label: 'Language Test Score', icon: FileText },
    { id: 'recommendation', label: 'Letter of Recommendation', icon: FileText },
    { id: 'cv', label: 'Curriculum Vitae', icon: FileText },
    { id: 'other', label: 'Other Documents', icon: FileText },
  ];

  // Function to handle file uploads
  const handleFileUpload = async (file, type) => {
    setUploading(true);
    try {
      // Simulate upload delay (use actual upload logic here)
      await new Promise(resolve => setTimeout(resolve, 1000));

      setDocuments(prev => [
        ...prev,
        {
          id: Date.now(),
          name: file.name,
          type: type,
          size: file.size,
          status: 'uploaded',
          date: new Date().toLocaleDateString(),
        }
      ]);
    } catch (error) {
      console.error('Error uploading file:', error);
    } finally {
      setUploading(false);
    }
  };

  // Handle file drop
  const handleDrop = React.useCallback((e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) {
      handleFileUpload(file, selectedDocumentType); // Use selected document type
    }
  }, [selectedDocumentType]);

  // Function to remove a document
  const removeDocument = (id) => {
    setDocuments(prev => prev.filter(doc => doc.id !== id));
  };

  // Handle document type selection
  const handleTypeSelection = (typeId) => {
    setSelectedDocumentType(typeId);
    fileInputRef.current?.click(); // Trigger file input after selecting the document type
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <Card>
        <CardHeader>
          <CardTitle className="text-2xl font-bold text-green-700">Academic Document Upload</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Upload Section */}
          <div 
            onDrop={handleDrop}
            onDragOver={(e) => e.preventDefault()}
            className="border-2 border-dashed border-green-200 rounded-lg p-8 text-center hover:border-green-400 transition-colors"
          >
            <FileUp className="w-12 h-12 mx-auto text-green-600 mb-4" />
            <h3 className="text-lg font-semibold mb-2">Drag and drop your files here</h3>
            <p className="text-sm text-gray-500 mb-4">or</p>
            <div className="flex gap-4 justify-center">
              <Button 
                onClick={() => fileInputRef.current?.click()}
                className="bg-green-600 hover:bg-green-700"
              >
                <Upload className="w-4 h-4 mr-2" />
                Choose File
              </Button>
              <Input 
                type="file" 
                ref={fileInputRef}
                className="hidden"
                onChange={(e) => {
                  if (e.target.files?.[0]) {
                    handleFileUpload(e.target.files[0], selectedDocumentType); // Pass selected type
                  }
                }}
              />
            </div>
          </div>

          {/* Document Type Selection */}
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
            {documentTypes.map((type) => (
              <Card 
                key={type.id}
                className="cursor-pointer hover:shadow-md transition-shadow"
                onClick={() => handleTypeSelection(type.id)} // Set the selected type
              >
                <CardContent className="flex items-center p-4 space-x-3">
                  <type.icon className="w-6 h-6 text-green-600" />
                  <span className="font-medium">{type.label}</span>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Uploaded Documents List */}
          {documents.length > 0 && (
            <div className="space-y-4">
              <h3 className="text-lg font-semibold text-green-700">Uploaded Documents</h3>
              {documents.map((doc) => (
                <Card key={doc.id} className="relative">
                  <CardContent className="flex items-center justify-between p-4">
                    <div className="flex items-center space-x-4">
                      <FileText className="w-6 h-6 text-green-600" />
                      <div>
                        <p className="font-medium">{doc.name}</p>
                        <p className="text-sm text-gray-500">
                          {(doc.size / 1024 / 1024).toFixed(2)} MB â€¢ {doc.date}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Check className="w-5 h-5 text-green-600" />
                      <button
                        onClick={() => removeDocument(doc.id)}
                        className="text-gray-400 hover:text-red-500 transition-colors"
                      >
                        <X className="w-5 h-5" />
                      </button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Upload Status */}
          {uploading && (
            <Alert className="bg-green-50 border-green-200">
              <AlertCircle className="w-4 h-4 text-green-600" />
              <AlertDescription>Uploading document...</AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default AcademicDocuments;
