import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import Button from '../ui/button';
import Input from '../ui/input';
import Label from '../ui/label';
import Alert from '../ui/alert';
import { FileUp, Plus, FileText, GraduationCap, X, Upload, Check, AlertCircle } from 'lucide-react';

const AcademicDocuments = () => {
  const [documents, setDocuments] = useState([]);
  const [uploading, setUploading] = useState(false);
  const fileInputRef = useRef(null);

  const [selectedDocumentType, setSelectedDocumentType] = useState('other');

  const documentTypes = [
    { id: 'transcript', label: 'Academic Transcript', icon: GraduationCap },
    { id: 'degree', label: 'Degree Certificate', icon: FileText },
    { id: 'language', label: 'Language Test Score', icon: FileText },
    { id: 'recommendation', label: 'Letter of Recommendation', icon: FileText },
    { id: 'cv', label: 'Curriculum Vitae', icon: FileText },
    { id: 'other', label: 'Other Documents', icon: FileText },
  ];

  // Handle file upload
  const handleFileUpload = async (file, type) => {
    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('document_type', type);  // Document type
      formData.append('document', file);  // File
      formData.append('name', file.name);  // File name
      formData.append('size', file.size);  // File size in bytes
      formData.append('date', new Date().toLocaleDateString());  // Upload date

      const response = await fetch('http://127.0.0.1:8000/academic-documents/', {
        method: 'POST',
        headers: {
          // 'Content-Type': 'multipart/form-data' is automatically set by fetch when using FormData
        },
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to upload file');
      }

      const responseData = await response.json();
      setDocuments((prevDocuments) => [
        ...prevDocuments,
        responseData,
      ]);
    } catch (error) {
      console.error('Error uploading file:', error);
    } finally {
      setUploading(false);
    }
  };

  // Handle file drop
  const handleDrop = useCallback((e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) {
      handleFileUpload(file, selectedDocumentType);  // Upload the file with selected type
    }
  }, [selectedDocumentType]);

  // Fetch documents
  const fetchDocuments = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/academic-documents/');
      if (!response.ok) {
        throw new Error('Failed to fetch documents');
      }

      const documentsData = await response.json();
      setDocuments(documentsData);  // Set the documents to state
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  // Fetch documents when component mounts
  useEffect(() => {
    fetchDocuments();
  }, []);

  // Remove document from state
  const removeDocument = (id) => {
    setDocuments(prev => prev.filter(doc => doc.id !== id));
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <Card>
        <CardHeader>
          <CardTitle className="text-2xl font-bold text-green-700">Academic Document Upload</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Upload Section */}
          <div onDrop={handleDrop} onDragOver={(e) => e.preventDefault()} className="border-2 border-dashed border-green-200 rounded-lg p-8 text-center hover:border-green-400 transition-colors">
            <FileUp className="w-12 h-12 mx-auto text-green-600 mb-4" />
            <h3 className="text-lg font-semibold mb-2">Drag and drop your files here</h3>
            <p className="text-sm text-gray-500 mb-4">or</p>
            <div className="flex gap-4 justify-center">
              <Button onClick={() => fileInputRef.current?.click()} className="bg-green-600 hover:bg-green-700">
                <Upload className="w-4 h-4 mr-2" />
                Choose File
              </Button>
              <Input 
                type="file" 
                ref={fileInputRef} 
                className="hidden" 
                onChange={(e) => { if (e.target.files?.[0]) handleFileUpload(e.target.files[0], selectedDocumentType); }} 
              />
            </div>
          </div>

          {/* Uploaded Documents List */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold text-green-700">Uploaded Documents</h3>
            {documents.map((doc) => (
              <Card key={doc.id} className="relative">
                <CardContent className="flex items-center justify-between p-4">
                  <div className="flex items-center space-x-4">
                    <FileText className="w-6 h-6 text-green-600" />
                    <div>
                      <p className="font-medium">{doc.name}</p>
                      <p className="text-sm text-gray-500">{(doc.size / 1024 / 1024).toFixed(2)} MB â€¢ {doc.date}</p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Check className="w-5 h-5 text-green-600" />
                    <button onClick={() => removeDocument(doc.id)} className="text-gray-400 hover:text-red-500 transition-colors">
                      <X className="w-5 h-5" />
                    </button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {/* Upload Status */}
          {uploading && (
            <Alert className="bg-green-50 border-green-200">
              <AlertCircle className="w-4 h-4 text-green-600" />
              <AlertDescription>Uploading document...</AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default AcademicDocuments;
