import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { X } from 'lucide-react';
import axios from 'axios';

const CampaignForm = ({ onCreate }) => {
  const { register, handleSubmit, formState: { errors }, reset } = useForm();
  const navigate = useNavigate();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState('');
  const [imagePreview, setImagePreview] = useState(null);
  const [imageFile, setImageFile] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const onSubmit = async (data) => {
  try {
    setIsSubmitting(true);
    setSubmitError('');

    // Extract and structure the payload as per campaignSchema
    const payload = {
      creator_id: JSON.parse(localStorage.getItem('user'))?._id, // Assuming user is stored in localStorage
      title: data.title,
      description: data.description || '', // Optional field
      status: data.status || 'draft', // Default to 'draft' if not provided
      start_date: data.start_date || null, // Convert empty values to null
      end_date: data.end_date || null, // Convert empty values to null
      target_goal: data.target_goal ? parseFloat(data.target_goal) : null, // Ensure number or null
      category: data.category || '',
      image_url: '', // Placeholder for image URL if backend sets it
      members: [], // Default empty array
      posts: [] // Default empty array
    };

    // If an image is uploaded, use FormData
    let formData;
    if (imageFile) {
      formData = new FormData();
      Object.keys(payload).forEach((key) => {
        formData.append(key, payload[key]);
      });
      formData.append('image', imageFile); // Attach the image
    } else {
      formData = payload;
    }

    // Configure headers
    const headers = {
      Authorization: `Bearer ${localStorage.getItem('token')}`,
      ...(imageFile ? { 'Content-Type': 'multipart/form-data' } : { 'Content-Type': 'application/json' }),
    };

    // Make the API request
    const response = await axios.post('http://localhost:5000/api/campaigns', formData, { headers });

    // Handle success
    if (response.data) {
      onCreate?.(response.data); // Call callback if provided
      reset(); // Reset form
      setImagePreview(null); // Reset image preview
      setImageFile(null); // Reset image file
    }
  } catch (error) {
    console.error('Error creating campaign:', error);
    setSubmitError(
      error.response?.data?.message ||
      error.message ||
      'Failed to create campaign. Please try again.'
    );
  } finally {
    setIsSubmitting(false);
  }
  };


  return (
    <div className="max-w-md mx-auto p-4">
      {submitError && (
        <div className="mb-4 p-2 bg-red-100 text-red-600 rounded-md text-sm">
          {submitError}
        </div>
      )}

      <form onSubmit={handleSubmit(onSubmit)}  className="w-1/3 mx-auto bg-white shadow-md rounded-lg p-6 space-y-4 border border-gray-300"
      style={{ minWidth: '300px', }}>
        <div>
          <button 
            onClick={() => navigate('/')}
            className="absolute top-2 right-2 p-1 rounded-full hover:bg-gray-100 transition-colors"
          >
            <X size={20} className="text-gray-600" />
          </button>
          <label className="block text-sm font-medium text-gray-700">Title</label>
          <input
            type="text"
            className="mt-1 p-1.5 w-full border border-gray-300 rounded-md text-sm"
            {...register('title', { required: 'Title is required' })}
          />
          {errors.title && <p className="text-red-500 text-xs">{errors.title.message}</p>}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Description</label>
          <textarea
            className="mt-1 p-1.5 w-full border border-gray-300 rounded-md text-sm"
            rows="3"
            {...register('description')}
          />
        </div>

        <div className="grid grid-cols-2 gap-3">
          <div>
            <label className="block text-sm font-medium text-gray-700">Status</label>
            <select
              className="mt-1 p-1.5 w-full border border-gray-300 rounded-md text-sm"
              {...register('status', { required: 'Status is required' })}
            >
              <option value="draft">Draft</option>
              <option value="active">Active</option>
              <option value="completed">Completed</option>
            </select>
            {errors.status && <p className="text-red-500 text-xs">{errors.status.message}</p>}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Category</label>
            <input
              type="text"
              className="mt-1 p-1.5 w-full border border-gray-300 rounded-md text-sm"
              {...register('category')}
            />
          </div>
        </div>

        <div className="grid grid-cols-2 gap-3">
          <div>
            <label className="block text-sm font-medium text-gray-700">Start Date</label>
            <input
              type="date"
              className="mt-1 p-1.5 w-full border border-gray-300 rounded-md text-sm"
              {...register('start_date')}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">End Date</label>
            <input
              type="date"
              className="mt-1 p-1.5 w-full border border-gray-300 rounded-md text-sm"
              {...register('end_date')}
            />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Target Goal</label>
          <input
            type="number"
            className="mt-1 p-1.5 w-full border border-gray-300 rounded-md text-sm"
            {...register('target_goal', { required: 'Target Goal is required' })}
          />
          {errors.target_goal && <p className="text-red-500 text-xs">{errors.target_goal.message}</p>}
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Image</label>
          <input
            type="file"
            accept="image/*"
            className="mt-1 p-1.5 w-full text-sm"
            onChange={handleImageUpload}
          />
          {imagePreview && (
            <div className="mt-2 w-32 h-32 overflow-hidden rounded-md">
              <img 
                src={imagePreview} 
                alt="Preview" 
                className="w-full h-full object-cover"
              />
            </div>
          )}
        </div>

        <div>
          <button
            type="submit"
            disabled={isSubmitting}
            className={`w-full py-1.5 px-4 bg-blue-600 text-white font-semibold rounded-md text-sm
              ${isSubmitting ? 'opacity-50 cursor-not-allowed' : 'hover:bg-blue-700'}`}
          >
            {isSubmitting ? 'Creating...' : 'Create Campaign'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CampaignForm;
